	public string getWord(int Diff)
	{
		string theWord = "";

		// 1 - uk
		// 2 - ro
		int lang = PlayerPrefs.GetInt("Language"); 

		if (Diff == 0)
			if (lang == 2)
				theWord = easyRo[Random.Range(0,easyRo.Length-1)];
			else
				theWord = easyEng[Random.Range(0,easyEng.Length-1)];

		if (Diff == 1)
			theWord = medium[Random.Range(0,medium.Length-1)];

		if (Diff == 2)
			if (lang == 2)
				theWord = masterRo[Random.Range(0,masterRo.Length-1)];
			else
				theWord = masterEng[Random.Range(0,masterEng.Length-1)];

		return theWord;
	}
